package training;

public class SkipMultiples {

	static void skipMultiplesOfFour() {
		System.out.println("the required output is : ");
		for(int i=0;i<100;i++) {
			if(i%4!=0)
			System.out.print(i+" ");
		}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		skipMultiplesOfFour();

		
	}

}
